(function(){const html=document.documentElement,contrastBtn=document.getElementById('contrast-toggle'),navToggle=document.getElementById('nav-toggle'),navList=document.getElementById('nav-list');contrastBtn.addEventListener('click',function(){const p=this.getAttribute('aria-pressed')==='true';this.setAttribute('aria-pressed',String(!p));if(!p)html.setAttribute('data-contrast','high');else html.removeAttribute('data-contrast')});navToggle.addEventListener('click',function(){const e=this.getAttribute('aria-expanded')==='true';this.setAttribute('aria-expanded',String(!e));if(!e)navList.style.display='flex';else navList.style.display='none'});const form=document.getElementById('contato-form'),formMsg=document.getElementById('form-msg');form.addEventListener('submit',function(e){e.preventDefault();formMsg.textContent='Mensagem enviada (simulada). Obrigado!';formMsg.classList.remove('sr-only');form.reset()});function t(e){'Tab'===e.key&&(document.body.classList.add('show-focus'),window.removeEventListener('keydown',t))}window.addEventListener('keydown',t)})();